import React,{useEffect, useState} from 'react'
import axios from 'axios';
import { useGlobalContext } from './context';
import { ImPhone, ImLocation } from "react-icons/im";

function RecommendedVenues() {
    const { location } = useGlobalContext();
    const [recommendedVenues,setRecommendedVenues]=useState([])

    useEffect(() => {
        axios.get(`https://api.foursquare.com/v2/venues/explore?client_id=LIEUSSAMWMOVTOT2NMXPTPQ1VJ5UIESU3EJKN53JV4QIKMZL&client_secret=FURPJP2L1EQJHTT3Y05RAEJOOLBPXTMALC1OJ3NQ2LAHYT5G&ll=${location.lat},${location.lng}&query=food&v=20189988&limit=10`)
            .then(res => {
                const recomVenues = res.data.response.groups;

                setRecommendedVenues(recomVenues)
                
            })
            .catch(err => {
                console.log(err)
            })
    }, [location.lat, location.lng])
    console.log(recommendedVenues);
    return (
        <div className='recommened-wrapper'>
            <h1>Recommended Venues</h1>
            {recommendedVenues.map(item=>{
                return <div className='recommended-inner'>
                    {item.items.map(i =>{
                        return <div className='recommended-item'>
                            <h1>{i.venue.name}</h1>
                            <p><i><ImPhone /></i>{i.venue.contact.phone}</p>
                            <div>
                                <i><ImLocation/></i><p>{i.venue.location.address}</p>
                                <p>{i.venue.location.postalCode}</p>
                                <p>{i.venue.location.city}</p>
                            </div>
                        </div>
                    })},
                    {item.items.map(e=>{
                        return <div className='recommended-tips'>
                            <p>{e.tips.text}</p>
                            <img src={e.tips.canonicalUrl} alt=""/>
                        </div>
                    })}
                    </div>
            })}
        </div>
    )
}

export default RecommendedVenues
