import React, {useState, useEffect} from 'react'
import axios from 'axios';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Map from './Map'

function Main() {
    const [venues, setVenues] = useState([])
    const [location,setLocation]=useState({lat:null,lng:null})
    const [input,setInput]=useState()


    const onTextSubmit = (e) => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(function (position) {
                setLocation( {lat: position.coords.latitude, lng: position.coords.longitude })
                console.log(location.lat);
            });
        }
        axios.get(`https://api.foursquare.com/v2/venues/search?client_id=LIEUSSAMWMOVTOT2NMXPTPQ1VJ5UIESU3EJKN53JV4QIKMZL&client_secret=FURPJP2L1EQJHTT3Y05RAEJOOLBPXTMALC1OJ3NQ2LAHYT5G&ll=${location.lat},${location.lng}&query=${input}&v=20189988&limit=1`)
            .then(res => {
                const venues = res.data.response.venues;
                console.log(venues);
                const {name,id}=venues[0]
                const newVenue={name,id}
                setVenues(newVenue)
            })
            .catch(err => {
                console.log(err)
            })
        console.log(input)
    }
  
    const { name,id } = venues
    return (
        <div>
            <p>{name}</p>
            <p>{id}</p>
            <Map
                center={location}
                markers={venues}
                containerElement={<div style={{ height: `400px`, width: `100%` }} />}
                mapElement={<div style={{ height: `100%`, width: `100%` }} />}
            />
            <TextField id="standard-basic" label="Search Cousine" value={input} onChange={e=>setInput(e.target.value)}/>
            <Button variant="contained" color="primary" href="#contained-buttons" onClick={onTextSubmit}>
                Search
      </Button>
        </div>
    )
}

export default Main
